import React, { Component } from "react";
import { Table } from 'antd';

const columns = [
    {
        title: 'Price',
        dataIndex: 'PriceAsString',
        key: 'PriceAsString',
    },
    {
        title: 'Ber Rating',
        dataIndex: 'BerRating',
        key: 'BerRating',
    },
    {
        title: 'Address',
        dataIndex: 'DisplayAddress',
        key: 'DisplayAddress',
    },
    {
        title: 'Living Area',
        dataIndex: 'SizeStringMeters',
        key: 'SizeStringMeters',
    },
    {
        title: 'Detail Link',
        dataIndex: 'BrochureUrl',
        key: 'BrochureUrl',
        render: url => {
            return <a href={"https://www.myhome.ie" + url}>MyHome</a>
        }
    },
    {
        title: 'Image',
        dataIndex: 'MainPhotoWeb',
        key: 'MainPhotoWeb',
        render: mediumImageLink => <img alt={mediumImageLink} src={mediumImageLink} />
    },
];


class Myhome extends Component {
    state = {
        resultCount: 0,
        SearchResults: [],
        priceChangeHistory: 0.0,
        priceChangeCount: 0
    };

    searchMyhome = async (event) => {
        event.preventDefault();
        let response = await fetch('/myhome');
        let body = await response.json();
        this.setState({ resultCount: body.ResultCount, SearchResults: body.SearchResults });
    }

    priceChangeMyhome = async (event) => {
        event.preventDefault();
        let response = await fetch('/myhomepricechange');
        let body = await response.json();
        let change = 0.0
        Object.keys(body.Results).map((e) => {
            change = change + body.Results[e].PriceChangePercentage
          });
        change = change / body.Results.length;
        this.setState({ priceChangeHistory: change });
        this.setState( {priceChangeCount: body.Results.length});

    }


    render() {
        return (
            <div class="tab">
                <button onClick={(event) => this.searchMyhome(event)}>Search</button>
                <h2>Properties Found {this.state.resultCount}</h2>
                <Table dataSource={this.state.SearchResults} columns={columns} />
                <button onClick={(event) => this.priceChangeMyhome(event)}>Price Change</button>
                <h2>Price Change from last {this.state.priceChangeCount} properites: {this.state.priceChangeHistory}</h2>
            </div>
        );
    }
}
export default Myhome
